SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant de presenter et d'etudier le sna,&lt;/span&gt;&lt;span &gt; nous jetterons les bases de quelques prerequis de neurophysiologie concernant les modalites de traitement de l'information au sein de ce systeme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
