SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;commencons tout d'abord par quelques prerequis concernant la &lt;/span&gt;&lt;span &gt;terminologie, l&lt;/span&gt;&lt;span &gt;'organisation de la moelle epiniere et quelques elements de base de la neurophysiologie. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
