// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le neurone&lt;/span&gt;&lt;span &gt; est l'unite fonctionnelle de base du systeme nerveux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'un point de vue structural on peut le decrire comme etant constitue d'un corps cellulaire (le soma) a partir duquel se trouvent plusieurs prolongements:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les dendrites, au niveau somatique et de l'extremite proximale, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'axone, a partir du cone d'emergence qui se termine, au niveau de son extremite distale, par la terminaison synaptique...&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la fonction essentielle du systeme nerveux est de traiter de l'information. le neurone, a cet egard aura donc a assurer le traitement et le transfert de l'information vers un autre neurone, ou une autre cible cellulaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'un point de vue fonctionnel, on peut donc schematiser le neurone par un pole recepteur de l'information et un pole emetteur.&lt;/span&gt;&lt;span &gt; attention, dans vos schemas a toujours bien differencier ces deux poles. les differentes fonctions du neurones par rapport a son role dans le traitement et le transfert de l'information sont assurees par une localisation precises de differentes proprietes metaboliques, d'excitabilite et de secretion.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
