SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les portions terminales des fibres post- ganglionnaires ne forment pas de synapses typiques, mais &lt;/span&gt;&lt;span &gt;présentent&lt;/span&gt;&lt;span &gt; des ramifications porteuses de renflements de 0,5 a 2 µm&lt;/span&gt;&lt;span &gt; le long de l'axone&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;appelés&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;varicosités&lt;/span&gt;&lt;span &gt;. ces &lt;/span&gt;&lt;span &gt;varicosités&lt;/span&gt;&lt;span &gt; se &lt;/span&gt;&lt;span &gt;succèdent&lt;/span&gt;&lt;span &gt; le long de la ramification et on dit qu'elles sont en chapelet. le neurotransmetteur est accumulé à l'&lt;/span&gt;&lt;span &gt;intérieur&lt;/span&gt;&lt;span &gt; des &lt;/span&gt;&lt;span &gt;varicosités&lt;/span&gt;&lt;span &gt; et il est &lt;/span&gt;&lt;span &gt;libére&lt;/span&gt;&lt;span &gt;́ quand un pa parcourt la portion terminale de la fibre. la &lt;/span&gt;&lt;span &gt;libération&lt;/span&gt;&lt;span &gt; est &lt;/span&gt;&lt;span &gt;schématisée&lt;/span&gt;&lt;span &gt; sur les deux &lt;/span&gt;&lt;span &gt;varicosités&lt;/span&gt;&lt;span &gt; de droite de la partie basse de la diapositive. cette disposition &lt;/span&gt;&lt;span &gt;confère&lt;/span&gt;&lt;span &gt; à l'action des fibres sympathiques post-ganglionnaires une plus grande &lt;/span&gt;&lt;span &gt;étendue&lt;/span&gt;&lt;span &gt; spatiale (en comparaison avec les synapses classiques, telles qu'entre neurones &lt;/span&gt;&lt;span &gt;pre&lt;/span&gt;&lt;span &gt;́- et post-ganglionnaires par exemple). &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette specialisation des terminaisons de fibres en chapelet de varicosites &lt;/span&gt;&lt;span &gt;est valable pour les fibres &lt;/span&gt;&lt;span &gt;postganglionnaires&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;symapthiques&lt;/span&gt;&lt;span &gt; (na) comme pour les fibres parasympathiques (&lt;/span&gt;&lt;span &gt;ach&lt;/span&gt;&lt;span &gt;) &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
