SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la fonction du systeme nerveux autonome est de maintenir a niveau constant l'environnement interne du corps c'est ce qu'on appelle egalement l‘homeostasie. ce systeme regule les fonctions involontaire telles que la pression sanguine,&lt;/span&gt;&lt;span &gt; la &lt;/span&gt;&lt;span &gt;frequence cardiaque, la respiration, l&lt;/span&gt;&lt;span &gt;a &lt;/span&gt;&lt;span &gt;temperature corporelle, les secretions glandulaires, la digestion et la reproduction. basee sur la nature de ses fonctions, le systeme nerveux autonome est egalement appele systeme nerveux involontaires ou vegetatif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
