SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;au dela des&lt;/span&gt;&lt;span &gt; principales differences entre le &lt;/span&gt;&lt;span &gt;snsomatique&lt;/span&gt;&lt;span &gt; et le &lt;/span&gt;&lt;span &gt;snautonome&lt;/span&gt;&lt;span &gt; resumees dans ce tableau comparatif, il est important de rajouter que le sns exerce un controle tres precis et rapide (de l'ordre de la msec) sur les organes cibles que sont les muscles squelettiques alors que le sna exerce un controle beaucoup plus diffus et plus lent (de l'ordre de la 100aine de msec a la sec) sur ses tissus cibles, en lien avec notamment le nombre de neurones, nature des fibres et des neurotransmetteurs utilises.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;paralysie flasque: caracterisee par une&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt; hypotonie musculaire avec abolition des reflexes tendineux et cutanes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
