SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'ensemble des r&lt;/span&gt;&lt;span &gt; adrenergiques &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;renine: synthese par les cellules de&lt;/span&gt;&lt;span &gt; l'appareil &lt;/span&gt;&lt;span &gt;juxta-glomerulaire du rein: &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;son role est de catalyser la transformation de l'&lt;/span&gt;&lt;span &gt;angiotensinogene&lt;/span&gt;&lt;span &gt; en angiotensine&lt;/span&gt;&lt;span &gt; i qui donnera l'angiotensine ii qui augmentera la volemie en jouant sur la sensation de soif, la secretion d'aldosterone et le calibre des vaisseaux sanguins pour repondre a une baisse de la pression de perfusion sanguine, une hyperkaliemie, une hyponatremie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;beta 2: &lt;/span&gt;&lt;span &gt;au niveau du poumon, ils provoquent une broncho-dilatation d'ou l'utilisation des β&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt;-mimetiques (sympathomimetique) dans l'asthme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au niveau des coronaires, ils provoquent une dilatation augmentant la perfusion cardiaque et permettent ainsi une augmentation de la frequence cardiaque du aux recepteurs β&lt;/span&gt;&lt;span &gt;1&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
