SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la plupart des organes internes etant innerve a la fois par des fibres sympathiques&lt;/span&gt;&lt;span &gt; et des fibres parasympathique, il existe donc une double innervation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette double innervation du sna va exercer un controle antagoniste, les effets du sympathique contrebalancant ceux du &lt;/span&gt;&lt;span &gt;syst&lt;/span&gt;&lt;span &gt; parasympathique en faisant des ajustement permanents et continus, d'ou la notion d'equilibre dynamique entre ces deux systemes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au repos et lors e la digestion les effets du systeme ps predominent alors que ceux de l'os predominent lors des reactions de fuite ou de combat.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
