SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce premier cours de neurophysiologie porte sur la physiologie du muscle strie, veritable organe moteur sous la controle du systeme moteur&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons detailler dans ce cours les elements indispensables a la comprehension de la physiologie du muscle strie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces notions sont necessaires pour comprendre le cours suivant, portant sur la motricite somatique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@objectifs pedagogiques du cours@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide1.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la physiologie du muscle fait intervenir, a l'etage cellulaire, un phenomene appele couplage excitation/contraction; ce phenomene permet de transformer un signal electrique (c'est a dire un pa qui chemine le long de l'axone du neurone moteur) en une contraction musculaire, entrainant donc un deplacement de fibres musculaires&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;differents facteurs vont alors influencer la force musculaire produite, facteurs qui seront detailles dans ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, les informations interessant la contraction musculaire vont faire l'objet d'un controle par le systeme nerveux (notamment le sn central). nous aborderons donc egalement le mode de codage de l'information sensitive concernant le muscle,  transmise au sn;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

