SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le muscle strie squelettique se distingue d'abord, d'un point de vue histologique, du muscle lisse par son caractere strie. il se differencie du muscle cardiaque, qui est egalement strie, par sa fixation au squelette par l'intermediaire de tendons&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'ensemble de la musculature striee squelettique remplit de ce fait plusieurs fonctions :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle permet le maintien du tonus musculaire, lors des contractions &lt;/span&gt;&lt;span  fontweight='bold'&gt;isometriques&lt;/span&gt;&lt;span &gt;, ce qui correspond a une fonction de motricite statique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;2. elle est a l'origine du mouvement, lors de contractions que l'on appelle &lt;/span&gt;&lt;span  fontweight='bold'&gt;isotoniques&lt;/span&gt;&lt;span &gt;. ceci correspond a la motricite dynamique, autrement dit le mouvement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;3. enfin, par la production et l'utilisation importante d'atp lors de la contraction musculaire, elle participe a la thermoregulation et au maintien a la balance energetique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
