SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le muscle strie est compose de differents tissus.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il comporte du tissu musculaire, sur lequel nous allons revenir, mais aussi du tissu conjonctif. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le tissu conjonctif a un role de soutien anatomique du tissu musculaire, permettant de solidariser les fibres musculaires entre elle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il contient egalement les structures vasculo-nerveuses a destination du muscle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, lorsqu'il est etire, le tissu conjonctif oppose une resistance a son etirement, ce qui engendre une tension. de ce fait, on dit que le tissu conjonctif est a l'origine des proprietes &lt;/span&gt;&lt;span  fontweight='bold'&gt;elastiques &lt;/span&gt;&lt;span &gt;du muscle&lt;/span&gt;&lt;span  fontweight='bold'&gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
