SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le tissu musculaire a proprement parler est compose de nombreux faisceaux c musculaires, regroupant chacun un grand nombre de fibres c musculaires &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au sein de chaque fibre musculaire se succedent des sarcomeres c,  veritables unites contractiles de base du muscle. chaque sarcomere contient des myofibrilles c de deux types, appeles filaments d'actine et de myosine.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
