SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces faisceaux musculaires vont pouvoir comporter des fibres musculaires de differents types ; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;on distingue les fibres de type i,&lt;/span&gt;&lt;span &gt; ou fibres oxydatives lentes, appelees encore fibres rouges. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce sont des fibres de petit diametre, riches en mitochondries, bien vascularisees. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces fibres de type i se caracterisent par leur potentiel de contraction modere,  leur vitesse de contraction moderee, et leurs grandes capacites oxydatives. ce sont ces fibres qui seront mises en jeu au cours d'efforts prolonges d'intensite moderee. a titre d'exemple, ce sont ces fibres qui seront presentes en grande quantite chez un sujet qui pratique le marathon.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

