SDpagenum++;
SDpagetitle[SDpagenum] = "@iii physiologie de la contraction musculaire@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide13.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les mouvements d'actine/myosine sont a l'origine d'une contraction musculaire, degageant une force musculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais cette contraction peut engendrer differents mouvements, a l'echelle du muscle dans son ensemble:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-elle peut etre isometrique, c'est a dire degager une force, sans modification de la longueur du muscle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-elle peut etre isotonique, c'est a dire a force de deplacement constante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- selon le type de mouvement provoque, on parle de contraction excentrique (le muscle s'allonge alors qu'il se contracte), ou, a l'inverse, de contraction concentrique, a l'origine d'un raccourcissement du muscle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

