SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide22.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans la plupart des cas, l'atp proviendra en fait du metabolisme aerobie, issue de la transformation du glucose, du glycogene ou d'acides gras en presence d'o2. cette voie de production a un bon rendement, ce qui signifie qu'elle est efficace : elle genere un nombre satisfaisant de molecules d'atp pour une molecule de glucose. mais cette voie aerobie a un debit faible, ce qui signifie que la vitesse de production de l'atp est tres faible et necessite un delai.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
