SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les muscles stries sont sous le controle de la moelle, qui va, par l'intermediaire de boucles reflexes, controler leur longueur et donc la force degagee. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'innervation sensitive du muscle a pour fonction principale de maintenir constant le tonus musculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette innervation sensitive depend  notamment des recepteurs sensitifs musculaires, appeles recepteurs fusoriaux, qui font partie des fuseaux neuro-musculaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les recepteurs tendineux, qui codent plus specifiquement pour la force du muscle, ne seront pas abordes dans le detail dans ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@vi innervation sensitive du muscle@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide26.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontweight='bold'&gt;certains recepteurs sensitifs, au sein meme du muscle, vont recueillir et coder des informations concernant la longueur et la vitesse de changement de longueur du muscle &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on trouve donc au sein du muscle strie, des organes sensitifs appeles fuseaux neuromusculaires (fnm). &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les fnm sont inseres entre les fibres musculaires, parallelement a celles-ci. ils sont constitues d'une dizaine de fibres, que l'on appelle fibres intra-fusales, entourees d'une capsule.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chacune de ces fibres intra-fusales est constituee de deux parties distinctes : une partie centrale, elastique, appelee zone equatoriale, et deux parties contractiles situees de part et d'autre de la zone equatoriale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au sein de la zone equatoriale se trouvent les recepteurs fusoriaux en tant que tel, qui sont innerves par des fibres sensitives myelinisees de gros diametre (de type ia).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@vi innervation sensitive du muscle@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide27.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les fibres ia sont enroulees autour de la zone equatoriale, et codent le signal transmis par les recepteurs fusoriaux. les fibres ia sont sensibles a deux parametres :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-  le degre d'etirement du muscle&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-  et la vitesse a laquelle s'effectue cet etirement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ainsi, pour un muscle donne, la frequence de base de decharge des fibres ia augmentera lorsque le muscle aura une longueur superieure a sa longueur de repos. lors de la phase dynamique d'etirement du muscle, la frequence des pa sera d'autant plus importante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
