SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les fibres ia etant sensibles a l'etirement musculaire et a la vitesse d'etirement, elles ne permettent pas de coder le raccourcissement du muscle pour le reguler. ce sera le role des motoneurones gamma, motoneurones de plus petit diametre, cheminant dans la corne ventrale aupres des motoneurones alpha, que de restaurer cette information.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
