SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;alors&lt;/span&gt;&lt;span &gt; que la physiologie du muscle en tant que telle a ete abordee dans le cours precedent, il est desormais indispensable de detailler les bases qui sous-tendant le fonctionnement du systeme moteur somatique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
