SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il&lt;/span&gt;&lt;span &gt; est indispensable, pour mieux comprendre le fonctionnement du systeme moteur, de bien maitriser l'anatomie de la voie pyramidale&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;celle-ci s'articule donc en deux faisceaux de neurones moteurs, egalement appeles motoneurones&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le premier motoneurone est central, puisque sa projection synaptique est situee dans le sns, au niveau de la corne ventrale de la moelle&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le second neurone prend sa racine dans la corne ventrale et projette sur le muscle via la jonction neuromusculaire et la plaque motrice&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
