SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'ensemble&lt;/span&gt;&lt;span &gt; des motoneurones de premier ordre forment un faisceau cortico-spinal&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les motoneurones prennent leur origine au niveau du cortex cerebral, au sein d'un gyrus appele gyrus precentral, puisqu'il est situe juste en avant du sillon central, encore appele sillon &lt;/span&gt;&lt;span &gt;rolandique&lt;/span&gt;&lt;span &gt;,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce cortex precentral est organise de facon &lt;/span&gt;&lt;span &gt;somatotopique&lt;/span&gt;&lt;span &gt;, ce qui signifie que chaque parcelle corticale contient des motoneurones qui vont commander une region donnee du corps (dans le cas de la &lt;/span&gt;&lt;span &gt;somatotopie&lt;/span&gt;&lt;span &gt; du cortex &lt;/span&gt;&lt;span &gt;rolandique&lt;/span&gt;&lt;span &gt;, chaque region corticale va commander les neurones a destination d'un groupe musculaire puisqu'il s'agit de motricite,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la distribution corticale de ces differentes aires est stereotypee, similaire d'un individu a l'autre,&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;et peut etre representee sous la forme d'un &lt;/span&gt;&lt;span &gt;homonculus&lt;/span&gt;&lt;span &gt;, appele &lt;/span&gt;&lt;span &gt;homonculus&lt;/span&gt;&lt;span &gt; de&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;penfield, du nom du neurologue americain qui l'a decrit.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous pouvez constater que la region corticale a destination du membre inferieur est peu etendue, situee a la face interne du cortex precentral, tandis que les neurones pyramidaux qui vont controler la motricite de la face, comme de la main occupent une grande partie de ce sillon&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette distribution tres inegale tient a la complexite des mouvements impliquant la face (et la main) comparativement a la motricite du membre inferieur,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
