SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le&lt;/span&gt;&lt;span &gt; second motoneurone, ici represente en rouge, presente donc son corps cellulaire dans la racine ventrale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;notez bien que ce motoneurone presente des afferences issues du 1&lt;/span&gt;&lt;span &gt;er&lt;/span&gt;&lt;span &gt; motoneurone, non representees sur ce schema, mais aussi des afferences sensitives qui lui parviennent par l'intermediaire d'un interneurone&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce motoneurone peripherique va emerger de la moelle par la racine ventrale, empruntera par la suite des plexus, puis projettera sur le muscle par l'intermediaire d'une jnm&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
