SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette voie motrice pyramidale est influencee&lt;/span&gt;&lt;span &gt; par differentes structures, qui vont moduler son activite, avant, et pendant le mouvement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'aire motrice supplementaire est la region corticale, situee aussi a la face interne (donc entre les hemispheres cerebraux) qui va &lt;/span&gt;&lt;span &gt;plannifier&lt;/span&gt;&lt;span &gt; le mouvement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais d'autres regions, comme les noyaux gris centraux, ilots profond de substance grise situes dans l'encephale, et le cervelet, vont projeter sur la voie pyramidale pour en modifier l'activite&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
