SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les arcs reflexes peuvent etre schematiquement classees en deux categories:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les reflexes d'etirement vont permettre le maintien du tonus musculaire, l'adaptation du tonus musculaire lors de &lt;/span&gt;&lt;span &gt;pertubations&lt;/span&gt;&lt;span &gt; rapides du mouvement, lors de la mise en mouvement d'un segment de membre&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les reflexes de defense, dont l'organisation est assez differente, vont permettre a l'organisme de se soustraire a un stimulus douloureux qui pourrait etre nocif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
