SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il&lt;/span&gt;&lt;span &gt; existe un second type de reflexe d'etirement, appele reflexe d'etirement tonique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce reflexe est declenche par la stimulation, mecanique, des organes tendineux de golgi (click)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;leur stimulation va entrainer des pa sur les fibres ib (click), projetant sur un interneurone medullaire (click), qui va lui-meme stimuler le motoneurone alpha, engendrant une contraction musculaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les reflexes d'etirement toniques sont mis en jeu dans la regulation du tonus musculaire de base qui sera adapte en &lt;/span&gt;&lt;span &gt;permancence&lt;/span&gt;&lt;span &gt; a la force musculaire percue par les organes tendineux de golgi&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
