SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la mise en jeu coordonnee de groupes musculaires agonistes et antagonistes&lt;/span&gt;&lt;span &gt; repose sur le principe de l'innervation reciproque&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en effet, lors d'un stimulus &lt;/span&gt;&lt;span &gt;dououreux&lt;/span&gt;&lt;span &gt;, les fibres nociceptives vont projeter sur un ensemble d'interneurones a un etage medullaire donne&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces interneurones sont excitateurs pour certains, inhibiteurs pour d'autre&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'activation d'une afference sensorielle va activer un groupe d'interneurones donnes, qui vont alors activer certains groupes musculaires, en inhiber certains, selon l'action de l'interneurone sur les motoneurones emergeant de la corne ventrale&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce reseau d'interneurones mis en jeu va permettre une coordination du mouvement, activant des muscles qui n'avaient pas subi de stimulation directe, mais qui vont potentialiser cette reponse en retrait&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette&lt;/span&gt;&lt;span &gt; innervation reciproque existe aussi pour les reflexes en flexion, ce qui va permettre une coordination, a l'etage medullaire, du tonus de differents segments de membre&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
