SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si le&lt;/span&gt;&lt;span &gt; mouvement est regule par les boucles reflexes, qui sont peripheriques, il existe egalement un controle central, notamment medullaire, du mouvement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce controle medullaire s'integre au principe d'autonomie, que l'on peut envisager comme une delocalisation positive de certaines fonctions&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en effet, le controle par la moelle de certaines activites motrices, initiees volontairement, mais poursuivies de facon automatique, va liberer l'encephale pour lui permettre de se consacrer a d'autres taches&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple, ce controle medullaire s'exercera lors de l'entretien de la marche.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
