SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le cortex moteur primaire est initiateur du mouvement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais il est sous la dependance d'autres  aires corticales, notamment du cortex &lt;/span&gt;&lt;span &gt;premoteur&lt;/span&gt;&lt;span &gt;, situe en avant du cortex moteur, et de l'aire motrice supplementaire, egalement situee en avant du cortex moteur, mais a la face interne des hemispheres cerebraux. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cortex &lt;/span&gt;&lt;span &gt;premoteur&lt;/span&gt;&lt;span &gt; comme l'ams presentent une organisation &lt;/span&gt;&lt;span &gt;somatotopique&lt;/span&gt;&lt;span &gt; qui leur est propre;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; quand on enregistre l'activite electrique, de l'aire 6,  au cours du mouvement, on recueille un pa precedant le pa du cortex moteur ; cette region est en effet impliquee dans la decision de mouvement, surtout en reponse a des elements externes, environnementaux. on peut parler de planification motrice en reponse a des stimuli externes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'aire motrice supplementaire est egalement impliquee dans la planification du mouvement, mais sa mise en jeu depend plus d'une decision de mouvement. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais le controle du mouvement n'est pas realise qu'au niveau du cortex, et implique egalement le cervelet et les noyaux gris centraux. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
