SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans un premier temps nous allons faire quelques rappels, sur les mecanismes de transport que sont la convection et la diffusion, ensuite nous allons parler de la barriere entre l'air et le sang, et enfin nous allons parler d'une loi physique importante a connaitre pour bien comprendre le processus de diffusion alveolo-capillaire, qu'est la loi de henry. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
