SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en revanche, vous vous souvenez qu'en raison de l'augmentation tres rapide du diametre des voies aeriennes que traverse l'air inspire, sa vitesse diminue au fur et a mesure des generations, et &lt;/span&gt;&lt;span &gt;lorsque'on&lt;/span&gt;&lt;span &gt; arrive au niveau des alveoles, la vitesse du flux de gaz (autrement dit la convection) est quasi nulle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
