SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;s'il y a 2 gaz dans un recipient, leur diffusion est independante l'une de l'autre : chaque gaz va diffuser selon son propre gradient de pression partielle, et jusqu'a l'equilibre. dans ce &lt;/span&gt;&lt;span &gt;shcema&lt;/span&gt;&lt;span &gt; vous voyez 2 gaz par exemple l'o2 et l'h2 dans 2 recipients separes par un robinet, au depart l'o2 est a droite et l'h2 est uniquement a &lt;/span&gt;&lt;span &gt;gache&lt;/span&gt;&lt;span &gt;, si on ouvre le robinet, chaque gaz va diffuer vers le cote ou sa concentration est plus basse. a la fin de ce processus, la concentration de chacun des gaz s'est egalisee dans les 2 recipients. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
