SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la concentration du gaz dans la phase liquide est proportionnelle a la px dans la phase gazeuse, ici j'augmente la px de l'o2 et au bout d'un temps, la concentration de l'o2 dissoute dans l'eau sera aura atteint un niveau plus eleve. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
