SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la loi de henry dit que la [c] du gaz dans la phase liquide depend de la px multipliee par un coefficient. ce coefficient est le coefficient de solubilite. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une consequence directe de la loi de henry est qu'on peut, et c'est habituellement le cas, exprimee la concentration d'un gaz dissoute en terme de pression partielle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple, si on dit que la pression partielle de l'o2 dans le sang arteriel est de 100 &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; ou 13.3 kpa,  cela signifie que si on met le sang en contact avec une phase gazeuse, a l'equilibre la px de l'o2 sera de 13.3 kpa dans cette phase gazeuse. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
