SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le coefficient de solubilite alpha depend de la nature du gaz, des proprietes &lt;/span&gt;&lt;span &gt;phsyico-chimiques&lt;/span&gt;&lt;span &gt; du solvant. il est exprime en ml gaz/ ml solvant / &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; (ou kpa). dans ce tableau vous avez des valeurs de &lt;/span&gt;&lt;span &gt;coeff&lt;/span&gt;&lt;span &gt; de &lt;/span&gt;&lt;span &gt;sollubilite&lt;/span&gt;&lt;span &gt; pour les gaz qui nous interessent &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; l'o2 et le co2 et l'azote. vous pouvez voir que le co2 est beaucoup plus soluble que l'o2 par exemple, lui-meme plus soluble que l'azote. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
