SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons brievement voir la diffusion du co2. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pco2  capillaire est initialement elevee a environ 45 &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; ou 6 kpa. la pco2 diminue rapidement et s'equilibre en &lt;/span&gt;&lt;span &gt;&lt;/span&gt;&lt;span &gt;0.25 s avec l'air alveolaire. au-dela de ce temps: plus d'echange de co&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; entre le gaz alveolaire et le sang capillaire, et c'est le renouvellement du sang capillaire qui permet aux echanges de continuer. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
