SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le premier&lt;/span&gt;&lt;span &gt; rappel concerne les pressions respiratoires. &lt;/span&gt;&lt;span &gt;en physiologie par convention on mesure les pressions par reference a la pression atmospherique, donc on considere que la pression atmospherique est nulle. en realite, une pression de zero n'existe pas, et la &lt;/span&gt;&lt;span &gt;patm&lt;/span&gt;&lt;span &gt; n'est pas nulle. mais arbitrairement, on designe la &lt;/span&gt;&lt;span &gt;patms&lt;/span&gt;&lt;span &gt; comme la pression de reference,&lt;/span&gt;&lt;span &gt; et &lt;/span&gt;&lt;span &gt;lorsque on exprime une pression de 0 ca veut dire qu'elle et egale a la pression atmospherique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de la meme facon, une pression negative veut dire qu'elle inferieure a la pression atmospherique. donc il ne s'agit pas de la valeur absolue de la pression mais relative a la&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;patm&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
