SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le second rappel concerne la loi de boyle. si on a un gaz dans un recipient ferme hermetiquement comme dans ce cylindre, alors a temperature egale, si on diminue le volume, la pression va augmenter et vice-versa. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;autrement dit, la pression varie en sens inverse du volume. dans la suite de ce chapitre, on va voir comment la loi de boyle s'applique au systeme respiratoire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
