SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;or, les alveoles ne sont pas dotees de contractilite ; la variation du volume pulmonaire est entierement passive et doit etre assuree par les structure &lt;/span&gt;&lt;span &gt;extrapulmonaires&lt;/span&gt;&lt;span &gt; ; c'est-a-dire la paroi thoracique et le diaphragme. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous savez, (je vous renvoie ici&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;au cours d'anatomie du thorax du prof &lt;/span&gt;&lt;span &gt;chaffanjon&lt;/span&gt;&lt;span &gt;), que le poumon relie a la paroi thoracique par l'intermediaire d'une cavite tres mince qui est cavite pleurale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
