SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme on va le voir, dans les conditions normales au cours de l'inspiration  c'est la baisse de la pression alveolaire qui fait rentrer l'air dans les alveoles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette baisse de la pression alveolaire est provoquee par l'augmentation du volume pulmonaire sous l'effet d'une expansion thoracique. quand le volume augmente, la pression diminue, on retrouve ici l'application de la loi de boyle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ici, a la difference du recipient qu'on a vu avant,&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt; le poumon n'est un espace clos, donc les variations de pression provoquent des mouvements d'air. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@pressions respiratoires@";
SDpagedocid[SDpagenum] = SDdocnum;
