SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voyons d'abord les muscles inspiratoires qui figurent en bleu sur ce schema. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les muscles inspiratoires comprennent le diaphragme, les m. intercostaux externes et les muscles accessoires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le &lt;/span&gt;&lt;span  fontweight='bold'&gt;diaphragme&lt;/span&gt;&lt;span &gt; est le muscle inspiratoire le plus important. il est constitue d'une mince couche musculaire disposee en dome, insere sur les cotes inferieures. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la contraction du diaphragme est commandee par les nerfs phreniques qui a&lt;/span&gt;&lt;span &gt; son origine au n. des &lt;/span&gt;&lt;span &gt;3e, 4e et 5&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; racines cervicales. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les autres muscles inspiratoires sont les &lt;/span&gt;&lt;span  fontweight='bold'&gt;intercostaux externes&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin les muscles accessoires comprennent les &lt;/span&gt;&lt;span  fontweight='bold'&gt;scalenes&lt;/span&gt;&lt;span &gt;, et le &lt;/span&gt;&lt;span  fontweight='bold'&gt;scm&lt;/span&gt;&lt;span &gt;. ces derniers muscles n'interviennent que lors d'une inspiration forcee, et se contractent tres peu lors d'une inspiration calme. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@muscles inspiratoires@";
SDpagedocid[SDpagenum] = SDdocnum;
