SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide17.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les parois thoraciques et le diaphragme delimitent un volume, qui est represente ici en coupe frontale a gauche, et en coupe sagittal a droite. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce volume est caracterise par 3 dimensions ou diametres : un diametre &lt;/span&gt;&lt;span &gt;cephalo-caudal&lt;/span&gt;&lt;span &gt; ou vertical chez un sujet debout, un diametre lateral et un diametre antero-posterieur qui ne peut pas etre observe sur la vue frontale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@muscles inspiratoires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide18.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;quand le diaphragme  se contracte, il descend et augmente ainsi la dimension verticale de la cage thoracique. l'autre consequence de la contraction diaphragmatique est qu'il refoule le contenu abdominal en bas et vers l'avant. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une&lt;/span&gt;&lt;span &gt; consequence de la contraction diaphragmatique est que la pression intra-abdominale augmente. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est pour cela que quand vous observez quelqu'un respirer, la paroi abdominale se deplace en avant lors de l'inspiration. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@muscles inspiratoires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide19.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voyons maintenant l'action des intercostaux externes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce schema montre la position des cotes par rapport a la colonne vertebrale sur une vue laterale et une coupe transversale. l'axe de rotation des cotes par rapport a vertebres dorsales est represente. en bleu vous voyez les muscles intercostaux externes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

