SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la contraction des intercostaux externes deplace les cotes vers le haut, ce qui augmente le diametre antero-posterieur mais aussi  transversal du thorax.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en plus, lors d'une inspiration forcee, la contraction des muscles scalenes eleve les deux premieres cotes, et celle du muscle scm contribue a l'elevation du sternum.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la contraction de ces muscles qu'on appelle &lt;/span&gt;&lt;span  fontweight='bold'&gt;accessoires&lt;/span&gt;&lt;span &gt; lors d'une inspiration forcee contribue donc a augmenter les dimensions  et le volume thoracique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@muscles inspiratoires@";
