SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide21.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans l'ensemble, la contraction du diaphragme et des muscles intercostaux externes lors d'une inspiration calme (c'est-a-dire non forcee) augmente les 3 dimensions thoraciques (vertical, &lt;/span&gt;&lt;span &gt;lat&lt;/span&gt;&lt;span &gt; et &lt;/span&gt;&lt;span &gt;ant&lt;/span&gt;&lt;span &gt;-post), ce qui a pour consequence d'augmenter le volume thoracique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

