SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en respiration calme, l'excursion du diaphragme est de 1 cm environ.&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais &lt;/span&gt;&lt;span &gt;lors d'une inspiration forcee, elle peut atteindre 10 cm. ici on voit une radiographie de face en expiration forcee ou maximale, et une radiographie chez le meme sujet a la fin d'une inspiration maximale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur cette radiographie en inspiration on a trace en pointille blanc le contour du poumon en expiration. vous pouvez voir le deplacement de la position du diaphragme sur la radio. on apercoit egalement l'augmentation du diametre lateral. on ne voit pas en revanche l'augmentation du diametre &lt;/span&gt;&lt;span &gt;ant&lt;/span&gt;&lt;span &gt;-post sur cette radio, qui est prise de face. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@muscles expiratoires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide23.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voyons maintenant les muscles expiratoires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un point tres important a souligner est qu'une expiration calme, ne necessite aucune contraction musculaire, on dit de ce fait qu'elle est passive. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme nous allons le voir plus loin, la cage thoracique est une structure elastique, c'est a dire que si on la deforme ou on modifie son volume, elle aura tendance a revenir a sa forme de depart. eh bien, c'est simplement la retraction elastique de l'ensemble su poumon et de a paroi thoracique qui est a l'origine de l'expiration calme. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par contre, lors d'une expiration active, par exemple a l'exercice, des muscles expiratoires sont mis en jeu. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

