SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voyons maintenant l'evolution des pressions au&lt;/span&gt;&lt;span &gt; cours du cycle respiratoire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur ce graphique, sont representees &lt;/span&gt;&lt;span  fontweight='bold'&gt;en haut &lt;/span&gt;&lt;span &gt;le volume qui entre et sort du poumon pendant chaque phase du cycle respiratoire, en suite la pression &lt;/span&gt;&lt;span &gt;intrapleurale&lt;/span&gt;&lt;span &gt;, et en dessous le debit de l'air. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous remarquerez que par convention le signe de ce debit est negatif quand l'air entre dans le poumon, au cours de l'inspiration et il est positif quand l‘air sort. enfin, c'est la pression alveolaire qui est representee toute en bas. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
