SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en realite, l'inspiration dure en moyenne 2 fois moins que l'expiration. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans ce schema vous voyez les durees reelles des phases inspiratoires et expiratoires. le temps inspiratoire appele ici ti, est environ la moitie du temps expiratoire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;donc ti est environ 1/3 de la duree totale du cycle, appele &lt;/span&gt;&lt;span &gt;ttot&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un autre point important est que la frequence respiratoire en cycles par minute peut etre calculee par 60/&lt;/span&gt;&lt;span &gt;ttot&lt;/span&gt;&lt;span &gt; en secondes. par exemple, si&lt;/span&gt;&lt;span &gt; la duree totale du cycle respiratoire est de 3 secondes, ca veut dire que la frequence respiratoire est de 60/3 = 20 cycles par minute. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
