SDpagedisplay[SDpagenum] = "Slide27.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;d'apres ce que nous venons de voir, on peut deduire&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;qu'il y a 2 facons d'augmenter la ventilation minute, ou debit ventilatoire total // : soit en augmentant la frequence respiratoire, soit en augmentant le volume courant. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais il faut noter que ces deux manieres d'augmenter la ventilation minute n'ont pas le meme impact sur la ventilation alveolaire. dans un premier exemple : nous allons augmenter la frequence respiratoire. si la frequence respiratoire augmente a 18 cycles par minute // par exemple, avec un vt toujours egal a 500 ml //, on a une ventilation minute qui est augmentee a 9000 ml/min, // ou 9 l/min. mais dans ce cas la ventilation de l'espace mort est egalement augmentee&lt;/span&gt;&lt;span &gt; //&lt;/span&gt;&lt;span &gt; puisqu'elle est de 150 ml x 18 : soit 2700 ml. // ce qui donne une ventilation alveolaire egale a 6300&lt;/span&gt;&lt;span &gt; ml&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
