SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;jusque la nous avons vu comment&lt;/span&gt;&lt;span &gt; l'air est deplace entre es alveoles et l'air ambiant. &lt;/span&gt;&lt;span &gt;nous allons maintenant voir la composition de l'air inspire, de l'air alveolaire et de l'air expire. l'objectif est de comprendre comment la ventilation alveolaire,&lt;/span&gt;&lt;span &gt; en renouvelant l'air dans les alveoles, determine les concentrations d'o2 et de co2 dans l'air alveolaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
