SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voyons maintenant la composition de l'air expire //. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;//vous voyez que la pression partielle d'o2 dans l'air expire, que l'on note &lt;/span&gt;&lt;span &gt;pgrande&lt;/span&gt;&lt;span &gt; o2, // est plus eleve que celle de l'air alveolaire du fait que ce gaz expire et un melange du gaz contenu dans l'espace mort (qui a une composition d'air inspire donc riche en o2 et quasiment depourvu de co2), et de l'air alveolaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour la meme raison, la peco2 // est plus basse que la paco2, &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; celle de l'air alveolaire. enfin l'air expire est toujours saturee en vapeur d'eau. //&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
