SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;de la meme facon que pour le co2, il existe&lt;/span&gt;&lt;span &gt; une relation entre la ventilation alveolaire et la pao2. &lt;/span&gt;&lt;span &gt;pour un meme&lt;/span&gt;&lt;span &gt; niveau de demande metabolique la pao2 depend : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'une part de la vitesse de reapprovisionnement de l'o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; : la ventilation alveolaire &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et par ailleurs de la vitesse de prelevement de l'o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; : la perfusion sanguine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'hyperventilation alveolaire augmente la p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 .&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;la p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;se rapproche alors de la &lt;/span&gt;&lt;span &gt;p&lt;/span&gt;&lt;span &gt;i&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;(air inspire).&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;augmente peut&lt;/span&gt;&lt;span &gt; augmenter egalement &lt;/span&gt;&lt;span &gt;si l'air inspire est enrichi en o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; : &lt;/span&gt;&lt;span &gt;dans&lt;/span&gt;&lt;span &gt; ce cas c'est la concentration &lt;/span&gt;&lt;span &gt;fractionaire&lt;/span&gt;&lt;span &gt; de l'o2 dans l'air inspire ou &lt;/span&gt;&lt;span &gt; f&lt;/span&gt;&lt;span &gt;i&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;qui augmente. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'hypoventilation alveolaire diminue la p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la baisse de p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;est proportionnelle au degre d'hypoventilation: &lt;/span&gt;&lt;span &gt;  &lt;/span&gt;&lt;span &gt;p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;co&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle peut etre corrigee par&lt;/span&gt;&lt;span &gt;&lt;/span&gt;&lt;span &gt; f&lt;/span&gt;&lt;span &gt;i&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;: principe de l'oxygenotherapie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
