SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le surfactant est compose a 85 - 90% de lipides et de 10 - 15 % de proteines. les lipide sont en majeure partie des phospholipides, dont 75% de &lt;/span&gt;&lt;span &gt;dipalmitoyl&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;phosphatidylcholine&lt;/span&gt;&lt;span &gt;. il existe quatre types de proteines differentes : spa, spb, spc et spd. le surfactant est produit par les cellules epitheliales alveolaires de type ii. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
